import { gql } from "@apollo/client";

export const ADD_CUSTOMER = gql`
  mutation addCustomer() {
    addCustomer(
  newcustomers:[
  {
        firstName: "Inge"
        lastName: "Vallaey"
        email: "l.vallaey@gmail.com"
    },
          {
        firstName: "Eline"
        lastName: "Vallaey"
        email: "eline.vallaey@gmail.com"
          }
    ]
  ){
    success
    customers{
      id
     	firstName
      lastName
    }
  }
  }
`;
