import "../App.css";
import { useQuery, useMutation } from "@apollo/client";
import { GET_CUSTOMERS } from "../graphql/queries";
import { ADD_CUSTOMER } from "../graphql/mutations";
import CustomerItem from "../components/customeritem";
import styled from "styled-components";
import React, { useEffect, useState } from "react";
import CSVReader from "react-csv-reader";
import { Formik } from "formik";

const CustomerTable = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 0px auto 0px auto;
`;

const CustomerHeader = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 40px auto 0px auto;
`;

const CsvBox = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  width: 80%;
  margin: 40px auto 0px auto;

  p {
    margin-right: 10px;
  }
`;

const CustomerAddButtons = styled.div`
  display: flex;
  flex-direction: row;
  width: 80%;
  margin: 40px auto 0px auto;
  button {
    cursor: pointer;
    background-color: #1c7293;
    color: white;
    width: 100%;
    height: 40px;
    margin: 20px;
    border: none;
    border-radius: 5px;
    outline: none;
    &:hover {
      background-color: #1f8db8;
    }
  }
`;
function CustomerAddPage() {
  const { loading, error, data } = useQuery(GET_CUSTOMERS);
  const [addone, setaddone] = useState(false);
  const [addmultiple, setaddmultiple] = useState(false);
  const [
    addCustomer,
    { loading: mutationLoading, error: mutationError, data: customerdata },
  ] = useMutation(ADD_CUSTOMER);

  const parseOptions = {
    header: true,
    dynamicTyping: true,
    skipEmptyLines: true,
    transformHeader: (header) => header.replace(/\W/g, "_"),
  };

  const handleCSV = (data, fileInfo) => {
    addCustomer({ variables: { newcustomers: data } });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <>
      {!addmultiple && !addone && (
        <CustomerAddButtons>
          <button onClick={() => setaddone(true)}>Add one</button>
          <button onClick={() => setaddmultiple(true)}>Add multiple</button>
        </CustomerAddButtons>
      )}

      {addmultiple && (
        <>
          <CsvBox>
            <p>Upload .CSV file:</p>
            <CSVReader
              cssClass="csv-reader-input"
              onFileLoaded={handleCSV}
              parserOptions={parseOptions}
              inputId="csvId"
              inputStyle={{ color: "black" }}
            />
          </CsvBox>
          {mutationLoading && <p>Processing and uploading data...</p>}
          {mutationError && <p>Error, Please try again</p>}
          {customerdata && <p>Customers succesfully added </p>}
        </>
      )}
      {addone && (
        <>
          <Formik
            initialValues={{ email: "", password: "" }}
            validate={(values) => {
              const errors = {};
              if (!values.email) {
                errors.email = "Required";
              } else if (
                !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
              ) {
                errors.email = "Invalid email address";
              }
              return errors;
            }}
            onSubmit={(values, { setSubmitting }) => {
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
              }, 400);
            }}
          >
            {({ isSubmitting }) => (
              <Form>
                <Field type="email" name="email" />
                <ErrorMessage name="email" component="div" />
                <Field type="password" name="password" />
                <ErrorMessage name="password" component="div" />
                <button type="submit" disabled={isSubmitting}>
                  Submit
                </button>
              </Form>
            )}
          </Formik>
        </>
      )}

      <CustomerHeader>
        <CustomerItem
          key={"header"}
          id={"ID"}
          firstName={"firstName"}
          lastName={"lastName"}
          email={"email"}
          customerSince={"customerSince"}
        ></CustomerItem>
      </CustomerHeader>
      <CustomerTable>
        {data.customers.map(
          ({ id, firstName, lastName, email, customerSince }) => (
            <CustomerItem
              key={id}
              id={id}
              firstName={firstName}
              lastName={lastName}
              email={email}
              customerSince={customerSince}
            ></CustomerItem>
          )
        )}
      </CustomerTable>
    </>
  );
}

export default CustomerAddPage;
