import React from "react";
import styled from "styled-components";

const CustomerContainer = styled.div`
  display: flex;
  flex-direction: row;
  padding: 5px;
  border: 1px solid black;
`;

const Item = styled.div`
  width: 20vw;
  padding: 2px;
  border-right: 1px solid black;
  p {
    margin: 5px;
    word-break: break-all;
  }
`;

const CustomerItem = ({ id, firstName, lastName, email, customerSince }) => {
  return (
    <>
      <CustomerContainer>
        <Item>
          <p>{id}</p>
        </Item>
        <Item>
          <p>{firstName}</p>
        </Item>
        <Item>
          <p>{lastName}</p>
        </Item>
        <Item>
          <p>{email}</p>
        </Item>
        <Item>
          <p>{customerSince}</p>
        </Item>
      </CustomerContainer>
    </>
  );
};

export default CustomerItem;
