import "./App.css";
import { useQuery, useMutation } from "@apollo/client";
import { GET_CUSTOMERS } from "./graphql/queries";
import { ADD_CUSTOMER } from "./graphql/mutations";
import CustomerItem from "./components/customeritem";
import styled from "styled-components";
import React, { useEffect } from "react";
import CSVReader from "react-csv-reader";

const CustomerTable = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 0px auto 0px auto;
`;
const CustomerHeader = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 40px auto 0px auto;
`;

const CsvBox = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 80%;
  margin: 0px auto 0px auto;
`;
function App() {
  const { loading, error, data } = useQuery(GET_CUSTOMERS);
  const [addCustomer] = useMutation(ADD_CUSTOMER);
  let arraynew = [
    {
      firstName: "Inge",
      lastName: "Vallaey",
      email: "eline.vallaey@gmail.com",
    },
    {
      firstName: "Louis",
      lastName: "Vallaey",
      email: "louis.vallaey@gmail.com",
    },
  ];
  const parseOptions = {
    header: true,
    dynamicTyping: true,
    skipEmptyLines: true,
    transformHeader: (header) => header.replace(/\W/g, "_"),
  };
  const handleCSV = (data, fileInfo) => {
    console.log(data);
    addCustomer({ variables: { newcustomers: data } });
  };

  useEffect(() => {
    addCustomer({ variables: { newcustomers: arraynew } });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <>
      <CsvBox>
        <CSVReader
          cssClass="csv-reader-input"
          label="Select CSV"
          onFileLoaded={handleCSV}
          parserOptions={parseOptions}
          inputId="csvId"
          inputStyle={{ color: "black" }}
        />
      </CsvBox>
      <CustomerHeader>
        <CustomerItem
          key={"header"}
          id={"ID"}
          firstName={"firstName"}
          lastName={"lastName"}
          email={"email"}
          customerSince={"customerSince"}
        ></CustomerItem>
      </CustomerHeader>
      <CustomerTable>
        {data.customers.map(
          ({ id, firstName, lastName, email, customerSince }) => (
            <CustomerItem
              key={id}
              id={id}
              firstName={firstName}
              lastName={lastName}
              email={email}
              customerSince={customerSince}
            ></CustomerItem>
          )
        )}
      </CustomerTable>
    </>
  );
}

export default App;
