import { gql } from "@apollo/client";

export const ADD_CUSTOMER = gql`
  input InputCustomer {
    firstName: String!
    lastName: String!
    email: String!
  }
  mutation addCustomer($newcustomers: [InputCustomer!]) {
    addCustomer(newcustomers: $newcustomers) {
      success
      customers {
        id
        firstName
        lastName
      }
    }
  }
`;
