import { gql } from "@apollo/client";

export const ADD_CUSTOMER = gql`
  mutation AddCustomer($newcustomers: Array!) {
    addCustomer(customers: $newcustomers) {
      success
      message
      customers {
        id
        customerSince
        firstName
        lastName
        email
      }
    }
  }
`;
