import "./App.css";
import { useQuery, gql } from "@apollo/client";

const GET_CUSTOMERS = gql`
  query Customers {
    customers {
      id
      firstName
      lastName
    }
  }
`;

function App() {
  const { loading, error, data } = useQuery(GET_CUSTOMERS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return data.customers.map(({ id, firstName, lastName }) => (
    <div key={id}>
      <p>
        {firstName} | {lastName}
      </p>
    </div>
  ));
}

export default App;
