import "./App.css";
import { useQuery, useMutation } from "@apollo/client";
import { GET_CUSTOMERS } from "./graphql/queries";
import { ADD_CUSTOMER } from "./graphql/mutations";
import CustomerItem from "./components/customeritem";
import styled from "styled-components";
import ReactFileReader from "react-file-reader";
import React, { useEffect } from "react";

const CustomerTable = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 40px auto 0px auto;
  border: 1px solid black;
`;

function App() {
  const Profiler = React.unstable_Profiler;
  const { loading, error, data } = useQuery(GET_CUSTOMERS);
  const [addCustomer] = useMutation(ADD_CUSTOMER);

  let arraynew = [
    {
      firstName: "Inge",
      lastName: "Vallaey",
      email: "l.vallaey@gmail.com",
    },
    {
      firstName: "Eline",
      lastName: "Vallaey",
      email: "eline.vallaey@gmail.com",
    },
  ];

  const handleFiles = (files) => {
    console.log(files[0]);
    var reader = new FileReader();
    reader.readAsText(files[0]);
  };

  useEffect(() => {
    console.log(arraynew);
    //addCustomer({ variables: { newcustomers: arraynew } });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <>
      <CustomerTable>
        {data.customers.map(
          ({ id, firstName, lastName, email, customerSince }) => (
            <CustomerItem
              key={id}
              id={id}
              firstName={firstName}
              lastName={lastName}
              email={email}
              customerSince={customerSince}
            ></CustomerItem>
          )
        )}
      </CustomerTable>
      <ReactFileReader handleFiles={handleFiles} fileTypes={[".csv"]}>
        <button className="btn">Upload</button>
      </ReactFileReader>
    </>
  );
}

export default App;
